#include<string>
#include<queue>
#include<iostream>
#include<unordered_map>
#include<stack>
using namespace std;

int optimizeIdentifiers(string s) 
{
    if (s.empty()) return 2;
    if (s.length() == 1) return 1;

    int left = 0;
    int right = s.length() - 1;

    int return_value = 0;
    int operations = 0;

    while(left <= right)
    {
        if(left == right && s[0] == 'i' && s[s.length() - 1] == 's')
        {
            return_value = operations;
            break;
        }

        if(left == right && s[0] == s[s.length() - 1] && s[left])
        {
            return_value = operations;
            break;
        }

        if(s[left] == s[0] && s[right] == s[s.length() - 1])
        {
            return_value = operations;
            left++;
            operations++;
            continue;
        }

        if(s[left] != s[0])
        {
            left++;
            operations++;
            continue;
        }

        if(s[right] != s[s.length() - 1])
        {
            right--;
            operations++;
            continue;
        }
    }

    return return_value;
}

int main()
{
    string s = "";
    optimizeIdentifiers(s);
    return 0;
}